import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { FaRobot, FaUser, FaPaperPlane, FaFilm, FaSync } from 'react-icons/fa';
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [serverStatus, setServerStatus] = useState('checking'); // 'checking', 'online', 'offline'
  const messagesEndRef = useRef(null);

  // Check server status on load
  useEffect(() => {
    checkServerStatus();
  }, []);

  const checkServerStatus = async () => {
    try {
      // Try to ping the server
      await axios.get('/api/ping', { timeout: 5000 });
      setServerStatus('online');
    } catch (error) {
      console.error('Server status check failed:', error);
      setServerStatus('offline');

      // Add a system message about server status
      const systemMessage = {
        text: "The server appears to be offline or not responding. Please check your connection and try again.",
        sender: 'bot',
        isError: true
      };

      setMessages(prev => [...prev, systemMessage]);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();

    if (!input.trim()) return;

    const userMessage = {
      text: input,
      sender: 'user'
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post('/api/chat', { message: input }, { timeout: 60000 });

      const botMessage = {
        text: response.data.reply,
        sender: 'bot'
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error sending message:', error);

      let errorMessage = 'Sorry, I encountered an error. Please try again later.';

      // Check for specific error types
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        if (error.response.data && error.response.data.error) {
          errorMessage = error.response.data.error;
        } else if (error.response.status === 503) {
          errorMessage = 'The AI model is currently loading. Please try again in a few moments.';
        } else if (error.response.status === 429) {
          errorMessage = 'Too many requests. Please wait a moment before trying again.';
        }
      } else if (error.request) {
        // The request was made but no response was received
        errorMessage = 'Could not connect to the server. Please check your internet connection and try again.';
        setServerStatus('offline');
      } else {
        // Something happened in setting up the request that triggered an Error
        errorMessage = 'An unexpected error occurred. Please try again.';
      }

      const botErrorMessage = {
        text: errorMessage,
        sender: 'bot',
        isError: true
      };

      setMessages(prev => [...prev, botErrorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const retryConnection = async () => {
    setServerStatus('checking');
    await checkServerStatus();
  };

  return (
    <AppContainer>
      <Header>
        <Logo>
          <FaFilm />
          <h1>Movie Chatbot</h1>
        </Logo>
        <Subtitle>Your AI Movie Expert</Subtitle>
        {serverStatus === 'offline' && (
          <ServerStatus>
            <span>Server Offline</span>
            <RetryButton onClick={retryConnection}>
              <FaSync />
            </RetryButton>
          </ServerStatus>
        )}
      </Header>

      <ChatContainer>
        <MessageList>
          <WelcomeMessage>
            <BotIcon><FaRobot /></BotIcon>
            <MessageContent>
              <p>Hello! I'm your movie expert chatbot. Ask me anything about the movies in my database!</p>
            </MessageContent>
          </WelcomeMessage>

          {messages.map((message, index) => (
            <Message key={index} sender={message.sender}>
              {message.sender === 'bot' ? (
                <BotIcon><FaRobot /></BotIcon>
              ) : (
                <UserIcon><FaUser /></UserIcon>
              )}
              <MessageContent isError={message.isError}>
                <p>{message.text}</p>
              </MessageContent>
            </Message>
          ))}

          {isLoading && (
            <Message sender="bot">
              <BotIcon><FaRobot /></BotIcon>
              <MessageContent>
                <TypingIndicator>
                  <span></span>
                  <span></span>
                  <span></span>
                </TypingIndicator>
              </MessageContent>
            </Message>
          )}

          <div ref={messagesEndRef} />
        </MessageList>

        <InputForm onSubmit={handleSendMessage}>
          <InputField
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask about movies..."
            disabled={isLoading || serverStatus === 'offline'}
          />
          <SendButton 
            type="submit" 
            disabled={isLoading || !input.trim() || serverStatus === 'offline'}
          >
            <FaPaperPlane />
          </SendButton>
        </InputForm>
      </ChatContainer>
    </AppContainer>
  );
};

// Styled Components
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 20px;
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  font-size: 1.5rem;
  color: #e50914;
`;

const Subtitle = styled.p`
  color: #aaa;
  font-size: 1rem;
`;

const ServerStatus = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  margin-top: 10px;
  color: #ff6b6b;
  font-size: 0.9rem;
`;

const RetryButton = styled.button`
  background: none;
  border: none;
  color: #aaa;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5px;
  border-radius: 50%;
  transition: all 0.2s;

  &:hover {
    color: #fff;
    background-color: rgba(255, 255, 255, 0.1);
  }
`;

const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  flex: 1;
  background-color: #1e1e1e;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.3);
`;

const MessageList = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const Message = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 10px;
  max-width: 80%;
  align-self: ${props => props.sender === 'user' ? 'flex-end' : 'flex-start'};
